// Transform Google Calendar event to Office 365 format
const event = $input.item.json;

// Build the event body with description
let bodyContent = '';

// Add original description if exists
if (event.description) {
  bodyContent += event.description;
}

// Add Google Meet/Hangouts link if available
if (event.hangoutLink) {
  bodyContent += '<br><br><hr>';
  bodyContent += '<p><strong>üé• Google Meet</strong></p>';
  bodyContent += `<p><a href="${event.hangoutLink}">${event.hangoutLink}</a></p>`;
}

// Add detailed conference data if available
if (event.conferenceData) {
  const conf = event.conferenceData;

  if (!event.hangoutLink && conf.entryPoints) {
    bodyContent += '<br><br><hr>';
    bodyContent += '<p><strong>üìÖ Conference Details</strong></p>';
  }

  if (conf.entryPoints && conf.entryPoints.length > 0) {
    for (const entry of conf.entryPoints) {
      if (entry.entryPointType === 'video' && entry.uri !== event.hangoutLink) {
        bodyContent += `<p>üìπ Video: <a href="${entry.uri}">${entry.uri}</a>`;
        if (entry.meetingCode) {
          bodyContent += ` (Code: ${entry.meetingCode})`;
        }
        bodyContent += '</p>';
      } else if (entry.entryPointType === 'phone') {
        bodyContent += `<p>üìû Phone: ${entry.label || entry.uri}`;
        if (entry.pin) {
          bodyContent += ` PIN: ${entry.pin}`;
        }
        bodyContent += '</p>';
      } else if (entry.entryPointType === 'sip') {
        bodyContent += `<p>üñ•Ô∏è SIP: ${entry.uri}</p>`;
      }
    }
  }

  if (conf.notes) {
    bodyContent += `<p><em>${conf.notes}</em></p>`;
  }
}

// Add sync tracking marker - use a simple, searchable format
const trackingId = event.id;
bodyContent += '<br><hr>';
bodyContent += `<p style="font-size:10px;color:#888;">[GCAL_SYNC_ID:${trackingId}]</p>`;

// Determine if this is an all-day event
const isAllDay = !!(event.start?.date && !event.start?.dateTime);

// Format dates
let startDateTime, endDateTime;
if (isAllDay) {
  startDateTime = event.start.date;
  endDateTime = event.end.date;
} else {
  startDateTime = event.start?.dateTime || event.start?.date;
  endDateTime = event.end?.dateTime || event.end?.date;
}

// Get timezone
const timeZone = event.start?.timeZone || Intl.DateTimeFormat().resolvedOptions().timeZone || 'UTC';

// Map show as status
let showAs = 'busy';
if (event.transparency === 'transparent') {
  showAs = 'free';
}

// Map sensitivity
let sensitivity = 'normal';
if (event.visibility === 'private' || event.visibility === 'confidential') {
  sensitivity = 'private';
}

return {
  json: {
    trackingId: trackingId,
    subject: event.summary || '(No Title)',
    bodyContent: bodyContent,
    isAllDay: isAllDay,
    startDateTime: startDateTime,
    endDateTime: endDateTime,
    timeZone: timeZone,
    location: event.location || '',
    showAs: showAs,
    sensitivity: sensitivity
  }
};

