// Find the matching Office 365 event by tracking ID
const response = $input.item.json;
const syncedEvents = response.value || [];
const transformedData = $('Transform for Update').item.json;
const trackingId = transformedData.trackingId;

// Log for debugging
console.log('Looking for tracking ID:', trackingId);
console.log('Number of Strike Team events found:', syncedEvents.length);

// Search ONLY in Strike Team events for our tracking ID
let outlookEventId = null;
for (const event of syncedEvents) {
  // Check in body content (text format requested)
  const bodyContent = event.body?.content || '';
  const bodyPreview = event.bodyPreview || '';

  // Look for our tracking marker in various formats
  const searchPattern = `GCAL_SYNC_ID:${trackingId}`;

  if (bodyContent.includes(searchPattern) || bodyPreview.includes(searchPattern)) {
    outlookEventId = event.id;
    console.log('Found matching event:', event.subject, event.id);
    break;
  }
}

if (!outlookEventId) {
  console.log('No matching event found for tracking ID:', trackingId);
}

return {
  json: {
    found: outlookEventId !== null,
    outlookEventId: outlookEventId,
    debugEventsCount: syncedEvents.length,
    searchedForId: trackingId,
    ...transformedData
  }
};

